package JavaLearning;

public class PrintOneToHunderedWithoutNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/**
		 * Output number from 1 to 100 without using any numbes in code
		 */

		int one = 'A' / 'A';	
		String s = "programmin";

		for (int i = one; i <= s.length() * s.length(); i++) {
			System.out.println(i);
		}
		
		System.out.println("-------------");
		
		for (int i = one; i <='d'; i++) {
			System.out.println(i);

		}
	}

}
